mapper.select:
select R.*, M.mem_nick 
from review_report R
	left outer join mem M on R.mem_no = M.mem_no 
where review_no = #{reviewNo} 
	and review_report_time >= #{reportStartTime}
order by review_report_no asc

mapper.update:
update review 
	set review_report_cnt = 0 
where review_no = #{reviewNo}
